<?phpclass home_frontend extends Controller {	function __construct(){		parent::__construct('', 'homeSkin');				$this->view->alias = 'Post';	}		function auto_run() {		global $request;				switch ($request->query['action']) {			default:				$this->loadDefault();		}	}		function loadDefault(){		global $app, $view;				$option = array();				$app->import('entity', array('post'));		$post = new post_entity();		$option['lastest'] = $post->getLastest(5);				$this->output = $this->view->loadDefault($option);	}}